// Dependencies / Components
import Product from "./Product";
import React from "react";
import { BrowserRouter as Router, Route, Link } from "react-router-dom";
import App from '../App'

// JSON
import products from "../products.json";

// CSS Files
import "../App.css";
import "../css/bootstrap.min.css";
import "../css/mdb.min.css";
import "../css/style.css";
import "../css/products.css";

class ProductsPage extends React.Component {
  state = {
    productsMap: products.products
  };

  sortByPriceHandler = () => {
    function compareByPrice(a, b) {
      const priceA = a.productPrice;
      const priceB = b.productPrice;
      return priceA - priceB;
    }
    this.setState({
      productsMap: this.state.productsMap.slice().sort(compareByPrice)
    });
  };
  sortByTypeHandler = () => {
    function compareByType(a, b) {
      const typeA = a.productType;
      const typeB = b.productType;
      if (typeA < typeB) {
        return -1;
      }
      if (typeA > typeB) {
        return 1;
      } else {
        return 0;
      }
    }
    this.setState({
      productsMap: this.state.productsMap.slice().sort(compareByType)
    });
  };
  unsortHandler = () => {
    this.setState({ productsMap: products.products });
  };

  render() {
    return (
      <div className="page-wrapper">
        <div className="container container-bg productsDiv">
          <div className="row sortedProductsRow">
            {this.state.productsMap.map((value, i) => {
              return (
                <Product
                  key={i}
                  productName={value.productName}
                  productPrice={value.productPrice}
                  productType={value.productType}
                  productDescription={value.productDescription}
                  productImage={value.productImage}
                  productImageAlt={value.productImageAlt}
                />
              );
            })}
          </div>
        </div>

        {/* <!-- Footer --> */}
        <footer className="page-footer font-small stylish-color-dark pt-4 align-items-end">
          {/* <!-- Footer Links --> */}
          <div className="container text-center text-md-left">
            {/* <!-- Grid row --> */}
            <div className="row">
              {/* <!-- Grid column --> */}
              <div className="col-md-4 mx-auto">
                {/* <!-- Content --> */}
                <h5 className="font-weight-bold text-uppercase mt-3 mb-4">
                  Disclaimer
                </h5>
                <p>
                  DB Electronics is a publicy traded company. Access to the
                  information and the documents contained on this portion of DB
                  Electronics' website is restricted for regulatory reasons.
                </p>
              </div>
              {/* <!-- Grid column --> */}

              <hr className="clearfix w-100 d-md-none" />

              {/* <!-- Grid column --> */}
              <div className="col-md-2 mx-auto">
                {/* <!-- Links --> */}
                <h5 className="font-weight-bold text-uppercase mt-3 mb-4">
                  News
                </h5>

                <ul className="list-unstyled">
                  <li>
                    <a href="#!">Sign up for our Newsletter!</a>
                  </li>
                  <li>
                    <a href="#!">DB News</a>
                  </li>
                  <li>
                    <a href="#!">Industry News</a>
                  </li>
                </ul>
              </div>
              {/* <!-- Grid column --> */}

              <hr className="clearfix w-100 d-md-none" />

              {/* <!-- Grid column --> */}
              <div className="col-md-2 mx-auto">
                {/* <!-- Links --> */}
                <h5 className="font-weight-bold text-uppercase mt-3 mb-4">
                  Legal
                </h5>

                <ul className="list-unstyled">
                  <li>
                    <a href="#!">Please</a>
                  </li>
                  <li>
                    <a href="#!">Don't</a>
                  </li>
                  <li>
                    <a href="#!">Sue</a>
                  </li>
                  <li>
                    <a href="#!">Me</a>
                  </li>
                </ul>
              </div>
              {/* <!-- Grid column --> */}

              <hr className="clearfix w-100 d-md-none" />

              {/* <!-- Grid column --> */}
              <div className="col-md-2 mx-auto">
                {/* <!-- Links --> */}
                <h5 className="font-weight-bold text-uppercase mt-3 mb-4">
                  Rewards
                </h5>

                <ul className="list-unstyled">
                  <li>
                    <a href="#!">Sign up!</a>
                  </li>
                  <li>
                    <a href="#!">Customer Deals</a>
                  </li>
                  <li>
                    <a href="#!">Member Deals</a>
                  </li>
                </ul>
              </div>
              {/* <!-- Grid column --> */}
            </div>
            {/* <!-- Grid row --> */}
          </div>
          {/* <!-- Footer Links --> */}

          {/* <!-- Call to action --> */}

          <hr />
          {/* <!-- Social buttons --> */}
          <ul className="list-unstyled list-inline text-center">
            <li className="list-inline-item">
              <a href="##" className="btn-floating btn-fb mx-1">
                <i className="fab fa-facebook-f"> </i>
              </a>
            </li>
            <li className="list-inline-item">
              <a href="##" className="btn-floating btn-tw mx-1">
                <i className="fab fa-twitter"> </i>
              </a>
            </li>
            <li className="list-inline-item">
              <a href="##" className="btn-floating btn-gplus mx-1">
                <i className="fab fa-google-plus-g"> </i>
              </a>
            </li>
            <li className="list-inline-item">
              <a href="##" className="btn-floating btn-li mx-1">
                <i className="fab fa-linkedin-in"> </i>
              </a>
            </li>
            <li className="list-inline-item">
              <a href="##" className="btn-floating btn-dribbble mx-1">
                <i className="fab fa-dribbble"> </i>
              </a>
            </li>
          </ul>
          {/* <!-- Social buttons --> */}

          {/* <!-- Copyright --> */}
          <div className="footer-copyright text-center py-3">
            Â© 2019 Copyright:
            <a href="./index.html"> DBElectronics.com</a>
          </div>
          {/* <!-- Copyright --> */}
        </footer>
        {/* <!-- Footer --> */}
      </div>
    );
  }
}

export default ProductsPage;
