// Dependencies / Components
import Product from "./Product";
import React from "react";
import { BrowserRouter as Router, Route, Link } from "react-router-dom";
import App from '../App'

// JSON
import products from "../products.json";

// CSS Files
import "../App.css";
import "../css/bootstrap.min.css";
import "../css/mdb.min.css";
import "../css/style.css";
import "../css/products.css";

class ProductsPage extends React.Component {
  state = {
    productsMap: products.products
  };

  sortByPriceHandler = () => {
    function compareByPrice(a, b) {
      const priceA = a.productPrice;
      const priceB = b.productPrice;
      return priceA - priceB;
    }
    this.setState({
      productsMap: this.state.productsMap.slice().sort(compareByPrice)
    });
  };
  sortByTypeHandler = () => {
    function compareByType(a, b) {
      const typeA = a.productType;
      const typeB = b.productType;
      if (typeA < typeB) {
        return -1;
      }
      if (typeA > typeB) {
        return 1;
      } else {
        return 0;
      }
    }
    this.setState({
      productsMap: this.state.productsMap.slice().sort(compareByType)
    });
  };
  unsortHandler = () => {
    this.setState({ productsMap: products.products });
  };

  render() {
    return (
      <div className="page-wrapper">
      {/* <Router>
      <Route path="/" component={App} />
      </Router> */}
        {/* NavBar */}
        <nav className="navbar navbar-expand-lg navbar-dark primary-color">
          <a className="navbar-brand" href="index.html">
            DB Electronics
          </a>

          {/* Collapse button */}
          <button
            className="navbar-toggler"
            type="button"
            data-toggle="collapse"
            data-target="#basicExampleNav"
            aria-controls="basicExampleNav"
            aria-expanded="false"
            aria-label="Toggle navigation"
          >
            <span className="navbar-toggler-icon" />
          </button>

          {/* <!-- Collapsible content --> */}
          <div className="collapse navbar-collapse" id="basicExampleNav">
            {/* <!-- Links --> */}
            <ul className="navbar-nav mr-auto">

              <li className="nav-item">
              <Link to='/' component={App} className='homeLink'>
                <a className="nav-link">
                  Home
                  <span className="sr-only">(current)</span>
                </a>
                </Link>
              </li>

              <li className="nav-item active">
                <a className="nav-link" href="./products.html">
                  Products
                </a>
              </li>
              <li className="nav-item">
                <a className="nav-link" href="./contact.html">
                  Contact Us!
                </a>
              </li>

              {/* <!-- Dropdown --> */}
            </ul>
            <ul className="navbar-nav sort">
              <li className="nav-item dropdown float-right">
                <a
                  href="##"
                  className="nav-link dropdown-toggle"
                  id="navbarDropdownMenuLink"
                  data-toggle="dropdown"
                  aria-haspopup="true"
                  aria-expanded="false"
                >
                  Sort
                </a>
                <div
                  className="dropdown-menu dropdown-primary"
                  aria-labelledby="navbarDropdownMenuLink"
                >
                  <a
                    href="##"
                    onClick={this.sortByTypeHandler}
                    className="dropdown-item"
                    id="typeSortDropdown"
                  >
                    Sort by Type
                  </a>
                  <a
                    href="##"
                    onClick={this.sortByPriceHandler}
                    className="dropdown-item"
                    id="priceSortDropdown"
                  >
                    Sort by Price
                  </a>
                  <a
                    href="##"
                    onClick={this.unsortHandler}
                    className="dropdown-item"
                    id="unsortDropdown"
                  >
                    Unsort
                  </a>
                </div>
              </li>
            </ul>
            {/* <!-- Links --> */}
          </div>
          {/* <!-- Collapsible content --> */}
        </nav>
        {/* NavBar */}
        {/* </Router> */}

        <div className="container container-bg productsDiv">
          <div className="row sortedProductsRow">
            {this.state.productsMap.map((value, i) => {
              return (
                <Product
                  key={i}
                  productName={value.productName}
                  productPrice={value.productPrice}
                  productType={value.productType}
                  productDescription={value.productDescription}
                  productImage={value.productImage}
                  productImageAlt={value.productImageAlt}
                />
              );
            })}
          </div>
        </div>

        {/* <!-- Footer --> */}
        <footer className="page-footer font-small stylish-color-dark pt-4 align-items-end">
          {/* <!-- Footer Links --> */}
          <div className="container text-center text-md-left">
            {/* <!-- Grid row --> */}
            <div className="row">
              {/* <!-- Grid column --> */}
              <div className="col-md-4 mx-auto">
                {/* <!-- Content --> */}
                <h5 className="font-weight-bold text-uppercase mt-3 mb-4">
                  Disclaimer
                </h5>
                <p>
                  DB Electronics is a publicy traded company. Access to the
                  information and the documents contained on this portion of DB
                  Electronics' website is restricted for regulatory reasons.
                </p>
              </div>
              {/* <!-- Grid column --> */}

              <hr className="clearfix w-100 d-md-none" />

              {/* <!-- Grid column --> */}
              <div className="col-md-2 mx-auto">
                {/* <!-- Links --> */}
                <h5 className="font-weight-bold text-uppercase mt-3 mb-4">
                  News
                </h5>

                <ul className="list-unstyled">
                  <li>
                    <a href="#!">Sign up for our Newsletter!</a>
                  </li>
                  <li>
                    <a href="#!">DB News</a>
                  </li>
                  <li>
                    <a href="#!">Industry News</a>
                  </li>
                </ul>
              </div>
              {/* <!-- Grid column --> */}

              <hr className="clearfix w-100 d-md-none" />

              {/* <!-- Grid column --> */}
              <div className="col-md-2 mx-auto">
                {/* <!-- Links --> */}
                <h5 className="font-weight-bold text-uppercase mt-3 mb-4">
                  Legal
                </h5>

                <ul className="list-unstyled">
                  <li>
                    <a href="#!">Please</a>
                  </li>
                  <li>
                    <a href="#!">Don't</a>
                  </li>
                  <li>
                    <a href="#!">Sue</a>
                  </li>
                  <li>
                    <a href="#!">Me</a>
                  </li>
                </ul>
              </div>
              {/* <!-- Grid column --> */}

              <hr className="clearfix w-100 d-md-none" />

              {/* <!-- Grid column --> */}
              <div className="col-md-2 mx-auto">
                {/* <!-- Links --> */}
                <h5 className="font-weight-bold text-uppercase mt-3 mb-4">
                  Rewards
                </h5>

                <ul className="list-unstyled">
                  <li>
                    <a href="#!">Sign up!</a>
                  </li>
                  <li>
